import React from 'react';
import {createStore} from "redux";

const init = {
    waiting: false,
    loggedIn: false
}
const sessionReducer = (state = {waiting: false, loggedIn: false}, action) => {
    switch (action.type) {
        case "attempt_login":
            return {
                waiting: true,
                loggedIn: false
            }
        case "login_success":
            return {
                waiting: false,
                loggedIn: true,
            }
    }
}

const store = createStore(sessionReducer);

export default store;
