import React from 'react';
import {createStore} from "redux";

const sessionReducer = (state, action) => {
    switch (action.type) {
        case "attempt_login":
            return {
                waiting: true,
                loggedIn: false
            }
        case "login_success":
            return {
                waiting: false,
                loggedIn: true,
            }
    }
}

const store = createStore(sessionReducer, {waiting: false, loggedIn: false});

export default store;
