import {fetchSession} from "../services/charger/fetchSessionService";

/**
 * @author Magnus
 * @version 06/08/2021
 * Dispatches actions to sessionReducer.
 * GetSession
 */

export const ACTION_TYPES = {
    FETCH_SESSION_PENDING: 'FETCH_SESSION_PENDING',
    FETCH_SESSION_SUCCESS: 'FETCH_SESSION:SUCCESS',
    FETCH_SESSION_FAILURE: 'FETCH_SESSION_FAILURE'
};

/**
 * Async method
 * Fetches current session and dispatches actions
 * in accord with response/error.
 * @return function
 */
export const getSession = () => {
    return async (dispatch) => {
        dispatch({type: ACTION_TYPES.FETCH_SESSION_PENDING, payload: null});
        fetchSession().then(response => {
            dispatch({type: ACTION_TYPES.FETCH_SESSION_SUCCESS, payload: response});
        }).catch(error => {
            dispatch({type: ACTION_TYPES.FETCH_SESSION_FAILURE, payload: error});
        });
    };
};
