import Overlay from "./Overlay";
import '../stylesheets/StartChargerOverlay.css';
import TimeSelector from "./TimeSelector";
import ChargerButton from "./ChargerButton";
import {useEffect, useState} from "react";
import getChargerOperatingMode from "../services/charger/getChargerOperatingMode";
import {STATES} from "../misc/constants";

const StartChargerOverlay = ({address, chargerId, onClickClose}) => {

    const [operatingMode, setOperatingMode] = useState(0);

    const updateOperatingMode = () => {

    }

    useEffect(() => {
        setTimeout(() => {
            getChargerOperatingMode(chargerId).then(x => {
                setOperatingMode(x.data.operatingMode);
            }).catch(() => {
               setOperatingMode(STATES.ERROR);
            });
        }, 10000);
    }, [operatingMode]);

    return (
        <Overlay content={
            <div style={{justifyContent: "space-evenly"}} className="centerFlex startChargerOverlay">
                <span onClick={() => onClickClose()} className="close">x</span>
                <h3>Start lading ved {address}</h3>
                <div style={{width: "100%"}}>
                    <h3 className="laddelGreen">Lad til</h3>
                    <TimeSelector/>
                </div>
                <ChargerButton operatingMode={operatingMode}/>
            </div>
        }/>
    )
}

export default StartChargerOverlay;
