import React from 'react'
import {useState, useEffect} from "react";

import '../stylesheets/ChargerButton.css'
import { STATES, getAnimState } from "../misc/constants";

/**
 * @author Magnus & Sven
 * Start/stop button for Charger
 *
 * @component
 */

const ChargerButton = (props) => {

    const [animState, setAnimState] = useState(getAnimState(STATES.WAITING));
    const [statusText, setStatusText] = useState("Venter på svar");
    const [disabled, setDisabled] = useState(true);


    useEffect(() => {
        /**
         * UseEffect listens to props.chargingState and updates ChargerButton based on that.
         *
         * @param {props.chargingState} int Status of charger
         * @memberOf ChargerButton
         */

        if (props.chargingState === STATES.WAITING ){
            setAnimState(getAnimState(STATES.WAITING));
            setStatusText("Venter på svar");
            setDisabled(true);
        }

        else if (props.chargingState === STATES.CARCONNECTED) {
            setAnimState(getAnimState(STATES.CARCONNECTED));
            setStatusText("Tilkoblet, start lading");
            setDisabled(false);
        }

        else if (props.chargingState === STATES.CHARGING) {
            setAnimState(getAnimState(STATES.CHARGING));
            setStatusText("Lader");
            setDisabled(false);
        }

        else if (props.chargingState === STATES.NOCARCONNECTED) {
            setStatusText("Bil ikke tilkoblet");
            setAnimState(getAnimState(STATES.NOCARCONNECTED));
            setDisabled(true);
        }
        else if(props.chargingState === STATES.ERROR) {
            setStatusText("Det oppstod en feil");
            setAnimState(getAnimState(STATES.ERROR));
            setDisabled(true);
        }
    }, [props.chargingState]);

    return (
        <div>
            <button id="startButton"
                    className={`round ${animState} chargerStatus`}
                    onClick={props.onClick}
                    disabled={disabled}>
                <h4>{statusText}</h4>
            </button>
        </div>
    )
}

export default ChargerButton;
