import doRequest from "../http/http";

/**
 * @author Sven
 */

/**
 * Attempts to find the latitude/longitude combination of an address
 * @param address the address
 * @return {Promise<any>}
 */
const Geocode = async (address) => {
    return await doRequest("GET",
        "http://open.mapquestapi.com/geocoding/v1/address?key=cPAuQRJNFCf5WEbYyaFMTBu1RNVg0Lw5&location="
        + encodeURI(address));
}

/**
 * Attemps to find the address of a latitude/longitude combination
 * @param lat latitude
 * @param lng longitude
 * @returns {Promise<any>}
 */
const ReverseGeocode = async (lat, lng) => {
    return await doRequest("GET",
        `http://open.mapquestapi.com/geocoding/v1/reverse?key=cPAuQRJNFCf5WEbYyaFMTBu1RNVg0Lw5&location=${lat},${lng}`)
}

/**
 * Attempts to get the user's geolocation
 * @returns {Promise<unknown>}
 */
const Geolocation = async () => {
    return await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
    });
}

export {Geolocation, Geocode, ReverseGeocode}
