import React, {useEffect, useState} from 'react';
import {MapConsumer, MapContainer, TileLayer} from 'react-leaflet';

import {Geolocation} from "../services/geo/geoService";

import ChargerMarker from "./ChargerMarker";

import 'leaflet/dist/leaflet.css';
import '../stylesheets/Map.css';
import '../stylesheets/Center.css';
import '../stylesheets/reservationOverlayContent.css';

import Overlay from "./Overlay";

Date.prototype.addHours = function (h) {
    this.setTime(this.getTime() + (h * 60 * 60 * 1000));
    return this;
}

Date.prototype.addMinutes = function (h) {
    this.setTime(this.getTime() + (h * 60 * 1000));
    return this;
}

/**
 * @author Magnus & Sven
 * @version 06/08/2021
 * Map for display and selection of chargers.
 * @param props
 * @component
 */

const Map = (props) => {

    const [location, setLocation] = useState([61.77735134683034, 9.350803488192016]);
    const [overlay, setOverlay] = useState(null);

    /**
     * Retrieves position of device on render.
     * @returns function
     * @memberOf Map
     */
    useEffect(() => {
        let isMounted = true;
        Geolocation
        ().then((loc) => {
            if (isMounted) setLocation([loc.coords.latitude, loc.coords.longitude]);
        }).catch((error) => {
            console.log(error.message);
        });
        return () => {
            isMounted = false
        };
    }, []);

    const onClickReserve = () => {
        setOverlay(<Overlay content={
            <div className="center" style={{backgroundColor: "white", width: "75%", height: "50%", borderRadius: "1em"}}>
                <span className="reservationOverlayLaddelLogo"/>
                <div className="reservationOverlayContent">
                    <span className="close" onClick={() => setOverlay(null)}>x</span>
                    <h3>Er du sikker på at du vil reservere denne laderen?</h3>
                </div>
            </div>
        }/>);
    }

    return (
        <div className="map">
            {overlay}
            <MapContainer center={location} zoom={7} scrollWheelZoom={true}>
                <TileLayer
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                    url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'
                />
                <MapConsumer>
                    {(map) => {
                        map.setView(location, map.getZoom());
                        return null;
                    }}
                </MapConsumer>
                {props.chargers.map((charger) =>
                    <ChargerMarker key={charger.chargerId}
                                   id={charger.chargerId}
                        // The date from the database is in UTC format, convert to the user's timezone
                                   scheduledEnd={charger.scheduledEnd != null ? new Date(charger.scheduledEnd).addMinutes(new Date().getTimezoneOffset()) : null}
                                   chargerName={charger.chargerName}
                                   id={charger.chargerId}
                                   active={charger.active}
                                   latitude={charger.latitude}
                                   longitude={charger.longitude}
                                   ownerUname={charger.ownerUname}
                                   address={charger.address}
                                   postalCode={charger.postalCode}
                                   city={charger.city}
                                   onClickReserve={() => onClickReserve()}

                    />
                )}
            </MapContainer>
        </div>
    )
}

export default Map;
