import React, {useState} from "react";
import {MapConsumer, MapContainer, TileLayer} from 'react-leaflet';

import vectorDistance from "../services/math/vectordistance";
import {Geocode, Geolocation, ReverseGeocode} from "../services/geo/geoService";

import AddressSelectorMarker from "../components/AddressSelectorMarker";
import AddressSearchBar from "../components/AddressSearchBar";

import '../stylesheets/RegisterChargerMapPage.css';
import history from "../history";

/**
 * @author Sven
 * @version 06/08/2021
 * Page for registering a new charger
 * @component
 */
const RegisterChargerMapPage = () => {

    const [position, setPosition] = useState([58.147582, 7.9971]);
    const [address, setAddress] = useState("Henter addresse...");
    const [zoom, setZoom] = useState(11);

    /**
     * Function that is called when a user submits an address in the address search bar
     * @param e - event
     * @return void
     * @memberOf RegisterChargerMapPage
     */
    const submitAddressSearchBar = (e) => {
        // Attempt to Geocode address (Convert sdsaddress to latitude and longitude)
        Geocode(e.address.value).then(x => {    
            let locations = JSON.parse(x.data).results[0].locations;

            // No address found
            if (locations.length === 0) {
                alert("Fant ikke adressen, vennligst prøv igjen.");
                return;
            }

            // Only one address found, set the position
            if (locations.length === 1) {
                let selectedPos = locations[0].latLng;

                setZoom(20);
                setPosition([selectedPos.lat, selectedPos.lng]);
                return;
            }

            // More than 1 position found
            Geolocation().then(x => {
                //Attempt to get the user's geolocation
                let a = {x: x.coords.latitude, y: x.coords.longitude};

                // Find the location closest to where the user currently is
                locations = locations.sort((x, z) => {
                    // If distance from a to x is less than distance from
                    // a to z, return -1
                    if (vectorDistance(a, {x: x.latLng.lat, y: x.latLng.lng}) <
                        vectorDistance(a, {x: z.latLng.lat, y: z.latLng.lng})) {
                        return -1;
                        // if distance from a to x is greater than distance from
                        // a to z, return 1
                    } else if (vectorDistance(a, {x: x.latLng.lat, y: x.latLng.lng}) >
                        vectorDistance(a, {x: z.latLng.lat, y: z.latLng.lng})) {
                        return 1;
                        // The vectors are equidistant
                    } else {
                        return 0;
                    }
                });

                setPosition([locations[0].latLng.lat, locations[0].latLng.lng]);
                setZoom(20);
            }).catch(() => {
                setZoom(20);
                setPosition([locations[0].latLng.lat, locations[0].latLng.lng]);
            });
        });
    }

    /**
     * Function that is called when AddressSelectorMarker gets dragged to a new position
     * @param e - event
     * @return void
     * @memberOf RegisterChargerMapPage
     */

    const dragend = (e) => {
        setPosition([e.lat, e.lng]);
        setZoom(20)
        ReverseGeocode(e.lat, e.lng).then(x => {
            try {
                let data = JSON.parse(x).results[0].locations[0];
                let address = data.street;

                // By default, leaflet puts the address number before the address name
                // which is incorrect in Norway, so if it contains a number we reorder the
                // string so that the number comes first
                if (containsNumber(address)) {
                    address = address.split(" ");
                    let addressStringBuilder = "";

                    for (let i = 1; i < address.length; i++) {
                        addressStringBuilder += (address[i] + " ");
                    }

                    addressStringBuilder += address[0];

                    setAddress(addressStringBuilder);
                } else {
                    setAddress(address);
                }
            } catch
                (error) {
                console.log(error);
                setAddress("Kunne ikke finne addressen...");
            }
        });
    }
    /**
     * Sends user to RegisterChargerPage and passes relevant parameters
     * to be parsed from URL.
     * @return void
     * @memberOf RegisterChargerMapPage
     */
    const selectAddress = () => {
        history.push(`/registercharger/${position[0]}/${position[1]}/${address}`)
    }

    return (
        <div className="content">
            <h1 style={{textAlign: "center"}} className={"laddelGreen"}>Legg til nytt ladepunkt</h1>
            <AddressSearchBar callback={(e) => submitAddressSearchBar(e)}
            />
            <div className="map">
                <MapContainer center={position} scrollWheelZoom={true}>
                    <TileLayer
                        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <MapConsumer>
                        {(map) => {
                            map.setView(position, zoom);
                            return null;
                        }}
                    </MapConsumer>
                    <AddressSelectorMarker position={position} address={address}
                        // Reverse GeoCode (Convert latitude and longitutde to address)
                                           dragend={(e) => dragend(e)}
                                           callback={() => selectAddress()}
                    />

                </MapContainer>
            </div>
        </div>
    );
}

const containsNumber = (string) => {
    return /\d/.test(string);
}

export default RegisterChargerMapPage;
