import React, {useEffect, useState} from 'react';
import {MapConsumer, MapContainer, Marker, Popup, TileLayer} from 'react-leaflet';
import { Link } from 'react-router-dom';
import 'leaflet/dist/leaflet.css';
import { Geolocation } from "../services/geo/geoService";
import {iconAvailable, iconUnavailable} from "../misc/leafletIcons";

import './Map.css';

Date.prototype.ddmmhhmm = function() {
    return (this.getDate() < 10 ? ("0" + this.getDate()) : this.getDate()) + "/" + ((this.getMonth()+1) < 10 ? ("0" + (this.getMonth()+1)) : (this.getMonth()+1)) + " " +
        (this.getHours() < 10 ? ("0" + this.getHours()) : this.getHours())
        + ":" + (this.getMinutes() < 10 ? ("0" + this.getMinutes()) : this.getMinutes());
}

Date.prototype.addHours = function(h) {
    this.setTime(this.getTime() + (h*60*60*1000));
    return this;
}

const Map = (props) => {

    const [location, setLocation] = useState([61.77735134683034, 9.350803488192016]);

    useEffect(() => {
        let isMounted = true;
        Geolocation
        ().then((loc) => {
            if(isMounted) setLocation([loc.coords.latitude, loc.coords.longitude]);
        }).catch((error) => {
            console.log(error.message);
        });
        return () => {isMounted = false};
    }, []);

    return (
        <div className="map">
            <MapContainer center={location} zoom={7} scrollWheelZoom={true}>
                <TileLayer
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                    url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'
                />
                <MapConsumer>
                    {(map) => {
                        map.setView(location, map.getZoom());
                        return null;
                    }}
                </MapConsumer>
                {props.chargers.map((charger) => {
                    console.log(charger);
                    console.log(charger.id);
                    console.log(charger.active);
                    console.log(charger.latitude);
                    console.log(charger.longitude);
                    return (
                        <Marker key={charger.id} icon={charger.active === 0 ? iconAvailable : iconUnavailable }
                                position={[charger.latitude, charger.longitude]}>
                            <Popup>
                                <span>
                                    <h5 className="laddelGreen">charger.ownerName</h5>
                                    <h5>{charger.address}</h5>
                                    {charger.active === 0 ?
                                        <Link to={`/charger/${charger.chargerId}`}>
                                            <button className="popupButton">Reserver</button>
                                        </Link> : null}
                                </span>
                            </Popup>
                        </Marker>
                    )
                    /**
                     *                     return (
                     <Marker key={charger.id} icon={charger.available === 0 ? iconUnavailable : iconAvailable}
                     position={charger.position}>
                     <Popup>
                     <span>
                     <h5 className={"laddelGreen"}>{charger.owner}</h5>
                     <h5>{charger.address}</h5>
                     <h5>{charger.endDate != null ? "Tilgjengelig " + charger.endDate.ddmmhhmm()
                                       : "Tilgjengelig nå"}</h5>
                     {charger.available === 1 ?
                                       <Link to={`/charger/${charger.chargerId}`}>
                                           <button className="popupButton">Reserver</button>
                                       </Link> : null}
                     </span>
                     </Popup>
                     </Marker>
                     )
                     */
                })}
            </MapContainer>
        </div>
    )
}

export default Map;
