import React, {useEffect, useRef, useState} from 'react';
import {Marker, Popup} from "react-leaflet";
import {Link} from "react-router-dom";

import getChargerOperatingMode from "../services/charger/getChargerOperatingMode";

import {STATES} from "../misc/constants";
import {iconAvailable, iconUnavailable, iconPending} from "../misc/leafletIcons";
import {startCharger} from "../services/charger/startChargingService";
import {connect} from "react-redux";
import {addMinutes, dateToHHMM, UTCToLocalTime} from "../services/time/timeService";

/**
 * A prototype that converts a date object to DDMMHHMM format
 * @return {string} a Date in DDMMHHMM string format
 */
Date.prototype.ddmmhhmm = function () {
    return (this.getDate() < 10 ? ("0" + this.getDate()) : this.getDate()) + "/" + ((this.getMonth() + 1) < 10 ? ("0" + (this.getMonth() + 1)) : (this.getMonth() + 1)) + " " +
        (this.getHours() < 10 ? ("0" + this.getHours()) : this.getHours())
        + ":" + (this.getMinutes() < 10 ? ("0" + this.getMinutes()) : this.getMinutes());
}

/**
 * @author Sven
 * @version 06/08/2021
 * A component for placing a marker on a map
 * @param props {address, active, chargerName, city, id, latitude, longitude, postalCode, scheduledEnd}
 */
const ChargerMarker = (props) => {

    const [active, setActive] = useState(-1);
    const [zIndexOffset, setZIndexOffset] = useState(0);
    const [icon, setIcon] = useState(iconUnavailable);
    const [popupContent, setPopupContent] = useState(null);

    useEffect(() => {
            if (props.active === 1) {
                if (props.session.session !== null && props.id === props.session.session.chargerID) {
                    if (props.scheduledEnd) {
                        setIcon(iconAvailable)
                        setZIndexOffset(3);
                        setPopupContent(<div>Du har en aktiv ladeÃ¸kt med denne laderen
                            <button className="popupButton">Vis ladeÃ¸kt</button>
                        </div>)
                    } else {
                        setZIndexOffset(3);
                        setIcon(iconPending);
                        setPopupContent(<div>Du har reservert denne laderen
                            <button onClick={() => {
                                props.handleClickStartCharger();
                            }} style={{backgroundColor: "#EAC43D"}} className="popupButton">Start lading</button></div>)
                    }
                } else if (props.scheduledEnd !== null) {
                    setIcon(iconUnavailable);
                    setZIndexOffset(1);
                    setPopupContent(
                        <div>Denne laderen er reservert til {props.scheduledEnd}
                        </div>);
                } else {
                    setZIndexOffset(1);
                    setIcon(iconUnavailable);
                    setPopupContent(<div>Denne laderen er reservert</div>)
                }
            } else if (props.active === 0) {
                setIcon(iconAvailable);
                if (props.session.session !== null) {
                    setZIndexOffset(2);
                    setPopupContent(<div>Du har allerede en ladeÃ¸kt</div>)
                } else {
                    setZIndexOffset(2);
                    setPopupContent(<div>Ledig, reserver <button onClick={() =>
                        props.handleClickReserve(props.id)} className="popupButton">Reserver</button></div>)
                }
            }
        }
        ,
        [props.session.session]
    );

    return (
        <Marker
            eventHandlers={{
                click: () => {
                    // Event handler for when you click on a marker
                    getChargerOperatingMode(props.id).then(x => { // Fetch the charger's operating mode
                        if (x.data.operatingMode !== STATES.NOCARCONNECTED && active !== 1) {
                            setActive(1);
                        }
                    })
                }
            }}
            icon={icon}
            zIndexOffset={zIndexOffset} // Render chargers that are available on top of unavailable chargers
            position={[props.latitude, props.longitude]}>
            <Popup>
                <span>
                    <h4 className="laddelGreen">{props.chargerName}</h4>
                    <h4>{props.address + ", " + props.postalCode + " " + props.city}</h4>
                    {popupContent}
                </span>
            </Popup>
        </Marker>
    )
}

export default ChargerMarker;
