import React, {useEffect, useRef, useState} from "react";
import roundToNearest5 from "../services/math/roundToNearest5";

const TimeSelector = () => {

    const [date, setDate] = useState(new Date());
    const [selectedHour, setSelectedHour] = useState(date.getHours());
    const [availableMinutes, setAvailableMinutes] = useState(null);

    const pollTime = setInterval(() => {
        const curDate = new Date();
        if (curDate.getMinutes() !== date.getMinutes()) {
            clearInterval(pollTime);
            setDate(new Date());
        }
    }, 1000);


    useEffect(() => {
        let isMounted = true;

        if(isMounted) {
            updateAvailableMinutes();
        }

        return (() => {
            clearInterval(pollTime);
        });
    }, []);

    useEffect(() => {
        let isMounted = true;

        if(isMounted) {
            updateAvailableMinutes();
        }

    }, [selectedHour]);

    const updateAvailableMinutes = () => {
        console.log(date.getHours() === selectedHour);
        let avail = [];
        switch(date.getHours() === selectedHour) {
            case true:
                for(let i = roundToNearest5(date.getMinutes()); i < 60; i+=5) {
                    avail.push(i);
                }
                setAvailableMinutes(avail);
                break;
            default:
                for(let i = 0; i < 60; i += 5) {
                    avail.push(i);
                }
                setAvailableMinutes(avail);
                break;
        }
    }

    return (
        <div>
            <select onChange={(e) => {
                setSelectedHour(e.target.value)
            }} style={{width: "33%"}} name="hour">
                {[...Array(9).keys()].map((hour) => {
                    return (
                        <option value={date.getHours() + hour}>{date.getHours() + hour}</option>
                    )
                })};
            </select>
            <h3 style={{display: "inline-block", marginLeft: "3%", marginRight: "3%"}}>:</h3>
            <select style={{width: "33%"}} name="minute">
                {availableMinutes.map((minute) => {
                    return (
                        <option value={minute}>{minute}</option>
                    )
                })};
            </select>
        </div>
    )
}

export default TimeSelector;
