import React, {useEffect, useRef, useState} from "react";
import roundToNearest5 from "../services/math/roundToNearest5";
import {addHours, nToNN} from "../services/time/timeService";

const TimeSelector = ({onChangeHour, onChangeMinute}) => {

    const [date, setDate] = useState(new Date());

    onChangeHour(date.getHours());
    onChangeMinute(date.getMinutes());

    const selectedDate = useRef(date);
    const [availableMinutes, setAvailableMinutes] = useState(null);

    const pollTime = setInterval(() => {
        const curDate = new Date();
        if (curDate.getMinutes() !== date.getMinutes()) {
            clearInterval(pollTime);
            setDate(new Date());
        }
    }, 1000);


    useEffect(() => {
        let isMounted = true;


        if(isMounted) {
            // To ensure that if the clock is for example 19:55, you can only start selecting from 20:00 and up
            if(date.getMinutes() + 5 >= 55) {

            }
            updateAvailableMinutes();
        }

        return (() => {
            clearInterval(pollTime);
            isMounted = false;
        });
    }, []);

    useEffect(() => {
        let isMounted = true;

        if(isMounted) {
            updateAvailableMinutes();
        }

        return (() => {
            isMounted = false;
        })

        console.log("HELLO")

    }, [selectedDate.current.getHours()]);

    const updateAvailableMinutes = () => {
        let avail = [];
        // DO NOT CHANGE TO ==
        switch(date.getHours() == selectedDate.current.getHours()) {
            case true:
                for(let i = roundToNearest5(date.getMinutes()) + 5; i < 60; i+=5) {
                    avail.push(i);
                }
                setAvailableMinutes(avail);
                break;
            default:
                for(let i = 0; i < 60; i += 5) {
                    avail.push(i);
                }
                setAvailableMinutes(avail);
                break;
        }
    }

    return (
        <div className="timeSelectorContainer">
            <select onChange={(e) => {
                selectedDate.current = new Date(e.target.value);
            }} style={{width: "33%"}} name="hour">
                {date.getMinutes() + 5 >= 55 ?
                    [...Array(9).keys()].map(x => {
                        return (
                            <option value={addHours(date, x + 1)}>{addHours(date, x + 1).toString()}</option>
                        )
                    })
                    :
                    [...Array(9).keys()].map(x => {
                        return (
                            <option value={addHours(date, x)}>{addHours(date, x).toString()}</option>
                        )
                    })
                }
            </select>
            <h3 style={{display: "inline-block", marginLeft: "3%", marginRight: "3%"}}>:</h3>
            <select onChange={() => {

            }} style={{width: "33%"}} name="minute">
                {availableMinutes !== null ?
                    availableMinutes.map((minute) => {
                        return (
                            <option key={minute} value={minute}>{nToNN(minute)}</option>
                        )
                    })
                    :
                    null
                }
            </select>
        </div>
    )
}

export default TimeSelector;
