import React, {useEffect, useState} from 'react';
import {MapConsumer, MapContainer, Marker, Popup, TileLayer} from 'react-leaflet';
import { Link } from 'react-router-dom';
import 'leaflet/dist/leaflet.css';
import { Geolocation } from "../services/geo/geoService";
import {iconAvailable, iconUnavailable} from "../misc/leafletIcons";

import './Map.css';

Date.prototype.ddmmhhmm = function() {
    return (this.getDate() < 10 ? ("0" + this.getDate()) : this.getDate()) + "/" + ((this.getMonth()+1) < 10 ? ("0" + (this.getMonth()+1)) : (this.getMonth()+1)) + " " +
        (this.getHours() < 10 ? ("0" + this.getHours()) : this.getHours())
        + ":" + (this.getMinutes() < 10 ? ("0" + this.getMinutes()) : this.getMinutes());
}

Date.prototype.addHours = function(h) {
    this.setTime(this.getTime() + (h*60*60*1000));
    return this;
}

const chargers = [
    {id: 1, position: [58.14415870673151, 7.99662286998717], available: 1, owner: "LADDEL AS", address: "Markens gate 4a, Kristiansand", startDate: null, endDate: null},
    {id: 2, position: [58.14866628520844, 7.996625316587957], available: 1, owner: "LADDEL AS", address: "Skippergata 74, Kristiansand", startDate: null, endDate: null},
    {id: 3, position: [58.14794514186687, 7.991068612145744], available: 1, owner: "LADDEL AS", address: "Kristian IVs gate 27, Kristiansand", startDate: null, endDate: null},
    {id: 4, position: [58.151897732474886, 8.004470646490908], available: 0, owner: "LADDEL AS", address: "Kjøita 42, Kristiansand", startDate: new Date(), endDate: new Date().addHours(15)},
    {id: 5, position: [58.1527832725409, 8.017556011065924], available: 1, owner: "LADDEL AS", address: "Tobienborgveien 10A, Kristiansand", startDate: null},
    {id: 6, position: [58.164020906402094, 8.003784299240747], available: 0, owner: "LADDEL AS", address: "Østsia, Kristiansand", startDate: new Date(), endDate: new Date().addHours(15)},
    {id: 7, position: [58.162608615357875, 8.00668229087177], available: 1, owner: "LADDEL AS", address: "Universitetsveien 13, Kristiansand", startDate: null, endDate: null},
    {id: 8, position: [58.161236238020805, 7.998647579225324], available: 1, owner: "LADDEL AS", address: "Arenfeldts vei 22, Kristiansand", startDate: null, endDate: null},
    {id: 9, position: [58.16374319292437, 8.005924748943608], available: 0, owner: "LADDEL AS", address: "Gimlemoen 24, Kristiansand", startDate: new Date(), endDate: new Date().addHours(15)},
    {id: 10, position: [58.16364739585768, 8.005769426485013], available: 0, owner: "LADDEL AS", address: "Gimlemoen 24, Kristiansand", startDate: new Date(), endDate: new Date().addHours(15)},
]

const Map = () => {

    const [location, setLocation] = useState([61.77735134683034, 9.350803488192016]);

    useEffect(() => {
        let isMounted = true;
        Geolocation
        ().then((loc) => {
            if(isMounted) setLocation([loc.coords.latitude, loc.coords.longitude]);
        }).catch((error) => {
            console.log(error.message);
        });
        return () => {isMounted = false};
    }, []);

    return (
        <div className="map">
            <MapContainer center={location} zoom={7} scrollWheelZoom={true}>
                <TileLayer
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                    url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'
                />
                <MapConsumer>
                    {(map) => {
                        map.setView(location, map.getZoom());
                        return null;
                    }}
                </MapConsumer>
                {chargers.map((charger) => {
                    return (
                        <Marker key={charger.id} icon={charger.available === 0 ? iconUnavailable : iconAvailable}
                                position={charger.position}>
                            <Popup>
                               <span>
                                   <h5 className={"laddelGreen"}>{charger.owner}</h5>
                                   <h5>{charger.address}</h5>
                                   <h5>{charger.endDate != null ? "Tilgjengelig " + charger.endDate.ddmmhhmm()
                                       : "Tilgjengelig nå"}</h5>
                                   {charger.available === 1 ?
                                       <Link to={`/charger/${charger.chargerId}`}>
                                           <button className="popupButton">Reserver</button>
                                       </Link> : null}
                               </span>
                            </Popup>
                        </Marker>
                    )
                })}
            </MapContainer>
        </div>
    )
}

export default Map;
