import { ACTION_TYPES } from "../actions/sessionActions";

const initState = {
    session: null,
    isLoading: false,
    loaded: false,
    fetchError: null
}

/**
 * @author Magnus
 * @version 06/08/2021
 * Method for updating session state
 * @param state - initial state
 * @param action - JavaScript-object w. action type and payload
 * @return state - updated state
 */

const sessionReducer = (state = initState, action) => {
    const payload = action.payload;
    switch (action.type) {
        case ACTION_TYPES.FETCH_SESSION_PENDING:
            console.log("fetching session...");
            return {
                ...state,
                isLoading: true,
                loaded: false,
                fetchError: null
            };
        case ACTION_TYPES.FETCH_SESSION_SUCCESS:
            console.log("success");
            return {
                ...state,
                session: payload,
                isLoading: false,
                loaded: true,
                fetchError: null
            };
        case ACTION_TYPES.FETCH_SESSION_FAILURE:
            console.log("failed");
            if (payload.response.status === 404) {
                return {
                    ...state,
                    session: null,
                    isLoading: false,
                    loaded: true,
                    fetchError: null
                }
            }
            return {
                ...state,
                session: null,
                isLoading: false,
                loaded: true,
                fetchError: payload
            };
        default:
            return state
    }
};

export default sessionReducer;
