import React, {useState, useEffect} from 'react';
import {addHours, addMinutes, nToNN, pollTime} from '../services/time/timeService'
import '../stylesheets/TimeSelector.css';

/**
 * @author Magnus & Sven
 *
 * Komponent som lar en velge tidspunkt.
 * @component
 */

const TimeSelector = (props) => {

    const [time, setTime] = useState(new Date());

    useEffect(() => {

        /**
         * Metode som setter tid i parent-komponent
         *
         * @memberOf TimeSelector
         * @returns {void}
         */

        props.setTime(time);

    }, [props.getTime]);

    pollTime(time, (e) => setTime(e));

    return (
        <div className="row">
            <div className="timeContainer">
                <div className="column">
                    <div className="timeController" onClick={() => setTime(addHours(time, 1))}>▲</div>
                    <h4>{nToNN(time.getHours())}</h4>
                    <div className="timeController" onClick={() => setTime(addHours(time, -1))}>▼</div>
                </div>

                <h4>:</h4>

                <div  className="column">
                    <div className="timeController" onClick={() => setTime(addMinutes(time, 5))} >▲</div>
                    <h4>{nToNN(time.getMinutes())}</h4>
                    <div className="timeController" onClick={() => setTime(addMinutes(time, -5))}>▼</div>
                </div>

            </div>
        </div>
    )
}

export default TimeSelector;
