import React, {useState} from 'react';
import {Marker, Popup} from "react-leaflet";
import {Link} from "react-router-dom";

import getChargerOperatingMode from "../services/charger/getChargerOperatingMode";

import {STATES} from "../misc/constants";
import {iconAvailable, iconUnavailable} from "../misc/leafletIcons";
import {startCharger} from "../services/charger/startChargingService";

/**
 * A prototype that converts a date object to DDMMHHMM format
 * @return {string} a Date in DDMMHHMM string format
 */
Date.prototype.ddmmhhmm = function () {
    return (this.getDate() < 10 ? ("0" + this.getDate()) : this.getDate()) + "/" + ((this.getMonth() + 1) < 10 ? ("0" + (this.getMonth() + 1)) : (this.getMonth() + 1)) + " " +
        (this.getHours() < 10 ? ("0" + this.getHours()) : this.getHours())
        + ":" + (this.getMinutes() < 10 ? ("0" + this.getMinutes()) : this.getMinutes());
}

/**
 * @author Sven
 * @version 06/08/2021
 * A component for placing a marker on a map
 * @param props {address, active, chargerName, city, id, latitude, longitude, postalCode, scheduledEnd}
 */
const ChargerMarker = (props) => {

    const [active, setActive] = useState(props.active);

    return (
        <Marker
            eventHandlers={{
                click: () => {
                    // Event handler for when you click on a marker
                    getChargerOperatingMode(props.id).then(x => { // Fetch the charger's operating mode
                        if (x !== STATES.NOCARCONNECTED && active !== 1) {
                            setActive(1);
                        }
                    })
                }
            }}
            icon={props.active === 0 ? iconAvailable : iconUnavailable}
            zIndexOffset={props.active === 0 ? 1 : 0} // Render chargers that are available on top of unavailable chargers
            position={[props.latitude, props.longitude]}>
            <Popup>
            <span>
                <h4 className="laddelGreen">{props.chargerName}</h4>
                <h4>{props.address + ", " + props.postalCode + " " + props.city}</h4>
                {active === 1 ?
                    <h4>Laderen er reservert til {props.scheduledEnd.ddmmhhmm()}</h4> : <h4>Laderen er ledig</h4>}
                {active === 0 ?
                    <button className="popupButton" onClick={() => props.handleClickReserve()}>Reserver</button>
                    : null}
                                    </span>
            </Popup>
        </Marker>
    )
}

export default ChargerMarker;
