import { ACTION_TYPES } from "../actions/mapActions";

const initState = {
    fetchError: null,
    isLoading: false,
    loaded: false,
    chargers: []
}

/**
 * @author Magnus
 * @version 06/08/2021
 * Method for updating map state
 * @param state - initial state
 * @param action - JavaScript-object w. action type and payload
 * @return state - updated state
 */
const mapReducer = (state = initState, action) => {
    const payload = action.payload;
    switch (action.type) {
        case ACTION_TYPES.FETCH_CHARGERS_PENDING:
            return {
                ...state,
                fetchError: null,
                isLoading: true,
                loaded: false
            }
        case ACTION_TYPES.FETCH_CHARGER_SUCCESS:
            return {
                ...state,
                chargers: payload,
                fetchError: null,
                isLoading: false,
                loaded: true
            }
        case ACTION_TYPES.FETCH_CHARGERS_FAILURE:
            return {
                ...state,
                chargers: [],
                fetchError: payload,
                isLoading: false,
                loaded: true
            }
        default:
            return state

    }
}

export default mapReducer;
