import React, {useEffect, useState} from 'react';
import {MapConsumer, MapContainer, Marker, Popup, TileLayer} from 'react-leaflet';
import { Link } from 'react-router-dom';
import 'leaflet/dist/leaflet.css';
import { Geolocation } from "../services/geo/geoService";
import {iconAvailable, iconUnavailable} from "../misc/leafletIcons";
import {STATES} from "../misc/constants";

import '../stylesheets/Map.css';
import getChargerOperatingMode from "../services/charger/getChargerOperatingMode";
import {login} from "../services/user/userService";
import ChargerMarker from "./ChargerMarker";

Date.prototype.ddmmhhmm = function() {
    return (this.getDate() < 10 ? ("0" + this.getDate()) : this.getDate()) + "/" + ((this.getMonth()+1) < 10 ? ("0" + (this.getMonth()+1)) : (this.getMonth()+1)) + " " +
        (this.getHours() < 10 ? ("0" + this.getHours()) : this.getHours())
        + ":" + (this.getMinutes() < 10 ? ("0" + this.getMinutes()) : this.getMinutes());
}

Date.prototype.addHours = function(h) {
    this.setTime(this.getTime() + (h*60*60*1000));
    return this;
}

const Map = (props) => {

    const [location, setLocation] = useState([61.77735134683034, 9.350803488192016]);

    useEffect(() => {
        let isMounted = true;
        Geolocation
        ().then((loc) => {
            if(isMounted) setLocation([loc.coords.latitude, loc.coords.longitude]);
        }).catch((error) => {
            console.log(error.message);
        });
        return () => {isMounted = false};
    }, []);

    return (
        <div className="map">
            <MapContainer center={location} zoom={15} scrollWheelZoom={true}>
                <TileLayer
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                    url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'
                />
                <MapConsumer>
                    {(map) => {
                        map.setView(location, map.getZoom());
                        return null;
                    }}
                </MapConsumer>
                {props.chargers.map((charger) => {
                   return <ChargerMarker
                        key={charger.id}
                        scheduledEnd={new Date(charger.scheduledEnd)}
                        id={charger.id}
                        active={charger.active}
                        latitude={charger.latitude}
                        longitude={charger.longitude}
                        ownerUname={charger.ownerUname}
                        address={charger.address}

                   />
                })}
            </MapContainer>
        </div>
    )
}

export default Map;
