import {iconAvailable} from "../misc/leafletIcons";
import {Marker, Popup} from "react-leaflet";
import React from "react";

const {useRef} = require("react");
const {useMemo} = require("react");

const AddressSelectorMarker = (props) => {
    //const [position, setPosition] = useState(props.center)
    const markerRef = useRef(null)
    const eventHandlers = useMemo(
        () => ({
            dragend(e) {
                const marker = markerRef.current
                e.target.openPopup();
                if (marker != null) {
                    props.dragend(marker.getLatLng());
                }
            },
        }),
        [],
    )

    return (
        <Marker
            draggable={true}
            icon={iconAvailable}
            eventHandlers={eventHandlers}
            position={props.position}
            ref={markerRef}>
            <Popup>
                <div><h3 className="laddelGreen">{props.address}</h3>
                    <h4>Dersom addressen ikke er korrekt kan dette endres på senere</h4>
                    <button onClick={() => props.callback()}
                            className="laddelGreen popupButton">Trykk her for å registrere laderen</button></div>
            </Popup>
        </Marker>
    )
}

export default AddressSelectorMarker;
