import React, {useEffect, useRef, useState} from "react";
import {connect, useDispatch} from "react-redux";

import history from "../history";

import {getSession} from "../actions/sessionActions";
import {getAllChargers} from "../actions/mapActions";

import Map from "../components/Map";
import ErrorBox from "../components/ErrorBox";

import '../stylesheets/ChargerMapPage.css';
import {StartCharger} from "../services/charger/startChargingService";
import {doPostAsync} from "../services/http/http";
import postPendingSession from "../services/charger/postPendingSession";
import getPendingSession from "../services/charger/getPendingSession";

/**
 * @author Magnus & Sven
 * @version 06/08/2021
 * Page with Map-component.
 * Allows user to navigate a map and view chargers + availability status
 * Displays whether user has an active charging session.
 * @param props
 * @component
 */
const ChargerMapPage = (props) => {
    const session = props.session;
    const mapError = props.map.fetchError;
    const sessionError = props.session.fetchError;
    const chargers = props.map.chargers;

    const dispatch = useDispatch();

    /**
     * Async function that dispatches actions for getting all chargers
     * and the users current session.
     * @returns void
     * @memberOf ChargerMapPage
     */
    const onLoad = async () => {
        await dispatch(getAllChargers());
        await dispatch(getSession());
    };

    const [overlay, setOverlay] = useState(null);

    useEffect(() => {
        let isMounted = true;
        if (!(props.map.isLoading || props.session.isLoading)) {
            onLoad();
        }
        return () => {
            isMounted = false;
        }
    }, []);

    /**
     * Sends the user to current active charger.
     * @returns void
     * @memberOf ChargerMapPage
     */
    const handleClick = () => {
        history.push(`/charger/${session.session.chargerID}`);
    }

    return (
        <div className="scopeContent">
            {overlay}
            <div className="box">
                <Map chargers={chargers}/>
                {sessionError || mapError ?
                    <ErrorBox error="Nettverksfeil. Trykk på knappen for å laste inn på nytt."
                              handleClick={() => onLoad()}/> : null}
                <div className="mapNavBar">
                    {session.session && session.isActive ?
                        <button style={{backgroundColor: "#EAC43D"}} className="sessionButton" onClick={() => handleClick()}>
                            <h4>Lader ved<br/>{session.session.address}</h4>
                        </button>
                        :
                        session.session && session.isPending ?

                            <button className="sessionButton" onClick={() => {

                            }}>
                                <h4>Start lading ved<br/>{session.session.address}</h4>
                            </button>
                            :
                            <div className="info">
                                <h4>Velg en ledig lader på kartet</h4>
                            </div>
                    }
                </div>
            </div>
        </div>

    );
}

const mapStateToProps = (state) => {
    return {
        session: state.session,
        map: state.map,
        auth: state.auth
    }
}

export default (connect)(mapStateToProps)(ChargerMapPage);
