import React, {useEffect, useState} from 'react';
import {MapConsumer, MapContainer, TileLayer} from 'react-leaflet';

import {Geolocation} from "../services/geo/geoService";

import ChargerMarker from "./ChargerMarker";

import 'leaflet/dist/leaflet.css';
import '../stylesheets/Map.css';
import {startCharger} from "../services/charger/startChargingService";
import ConfirmOverlay from "./ConfirmOverlay";
import Overlay from "./Overlay";
import postPendingSession from "../services/charger/postPendingSession";
import {useDispatch} from "react-redux";
import {getSession} from "../actions/sessionActions";
import {dateToHHMM, UTCToLocalTime} from "../services/time/timeService";

Date.prototype.addHours = function (h) {
    this.setTime(this.getTime() + (h * 60 * 60 * 1000));
    return this;
}

Date.prototype.addMinutes = function (h) {
    this.setTime(this.getTime() + (h * 60 * 1000));
    return this;
}

/**
 * @author Magnus & Sven
 * @version 06/08/2021
 * Map for display and selection of chargers.
 * @param props
 * @component
 */

const Map = (props) => {

    const [location, setLocation] = useState([61.77735134683034, 9.350803488192016]);
    const [overlay, setOverlay] = useState(null);
    const dispatch = useDispatch();

    console.log(props.chargers);
    /**
     * Retrieves position of device on render.
     * @returns function
     * @memberOf Map
     */
    useEffect(() => {
        let isMounted = true;
        Geolocation
        ().then((loc) => {
            if (isMounted) setLocation([loc.coords.latitude, loc.coords.longitude]);
        }).catch((error) => {
            console.log(error.message);
        });
        return () => {
            isMounted = false
        };
    }, []);

    const handleClickReserve = (e) => {
        console.log(e);
        setOverlay(<ConfirmOverlay content={<div>
            <h3>Er du sikker på at du vil reservere?</h3>
            <h5>Du vil ha muligheten til å starte laderen innen 15 min</h5>
            <h5>Tjenesten medfører et reservasjonsgebyr
                på 10 kr</h5></div>}
                                   onClickClose={() => setOverlay(null)}
                                   onClickCancel={() => setOverlay(null)}
                                   onClickAccept={() => {
                                       postPendingSession(e.target.value).then(x => {
                                           dispatch(getSession());
                                       }).catch(error => {
                                           console.log(error.response);
                                       })
                                       setOverlay(
                                           <Overlay content={
                                               <div className="centerFlex"
                                                    style={{
                                                        width: "60%", height: "30%",
                                                        display: "block", backgroundColor: "white", borderRadius: "1em"
                                                    }}>
                                                   <span className="close" onClick={() => setOverlay(null)}>x</span>
                                                   <h3>Vi fikk penga, bro</h3>
                                                   <span style={{height: "10em", width: "10em"}}
                                                         className="checkMarkAnim"/>
                                               </div>}/>);
                                   }}
        />);
    }

    return (
        <div className="map">
            {overlay}
            <MapContainer center={location} zoom={7} scrollWheelZoom={true}>
                <TileLayer
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                    url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'
                />
                <MapConsumer>
                    {(map) => {
                        map.setView(location, map.getZoom());
                        return null;
                    }}
                </MapConsumer>
                {props.chargers.map((charger) =>
                    <ChargerMarker key={charger.chargerId}
                                   id={charger.chargerId}
                        // The date from the database is in UTC format, convert to the user's timezone
                                   scheduledEnd={charger.scheduledEnd != null ? dateToHHMM(UTCToLocalTime(new Date(charger.scheduledEnd))) : null}
                                   chargerName={charger.chargerName}
                                   active={charger.active}
                                   latitude={charger.latitude}
                                   longitude={charger.longitude}
                                   ownerUname={charger.ownerUname}
                                   address={charger.address}
                                   postalCode={charger.postalCode}
                                   city={charger.city}
                                   handleClickReserve={(e) => handleClickReserve(e)}
                                   session={props.session}
                    />
                )}
            </MapContainer>
        </div>
    )
}

export default Map;
