import React, {useState, useEffect} from 'react';
import {addHours, addMinutes, nToNN, pollTime} from '../services/time/timeService'
import '../stylesheets/TimeSelector.css';

/**
 * @author Magnus & Sven
 *
 * Komponent som lar en velge tidspunkt.
 * @component
 */

const TimeSelector = (props) => {

    const [time, setTime] = useState(new Date());

    let curDate = new Date();

    const getAvailableMinutes = () => {
        let ret = [];
        if(hour === curDate.getHours()) {
            for(let i = curDate.getMinutes(); i <= 59; i++) {
                ret.push(i);
            }
            return ret;
        }
        return [{...Array(60).keys()}];
    }

    const [hour, setHour] = useState(curDate.getHours());

    const [availableMinutes, setAvailableMinutes] = useState(getAvailableMinutes());

    useEffect(() => {

        /**
         * Metode som setter tid i parent-komponent
         *
         * @memberOf TimeSelector
         * @returns {void}
         */
        let isMounted = true;

        if(isMounted) {
            props.setTime(time);
        }

        return () => {
            isMounted = false;
        }
    }, [props.getTime]);

    pollTime(time, (e) => setTime(e));

    return (
        <div className="row">
            <div className="timeContainer">
                <div className="column">
                    <select onChange={(e) => {
                        setHour(e);
                        setAvailableMinutes(getAvailableMinutes());
                    }} name="hours" value={nToNN(curDate.getHours())}>
                        {[...Array(curDate.getHours()+9).keys()].map(x => {
                            return (
                                <option value={nToNN(x)}>{nToNN(x)}</option>
                            )
                        })}
                    </select>
                </div>

                <h4>:</h4>

                <div  className="column">
                    <select name="minutes" value={nToNN(curDate.getMinutes())}>
                        {availableMinutes.map((x) => {
                            return (
                                <option value={nToNN(x)}>{nToNN(x)}</option>
                            )
                        })}
                    </select>
                </div>

            </div>
        </div>
    )
}

export default TimeSelector;
