/**
 * @author Magnus
 * @version 06/08/2021
 * Metode som henter JWT fra user i localStorage
 * @returns JavaScript-object - Header til Http-request eller tom kropp
 */

const authHeader = () => {
    const token = JSON.parse(localStorage.getItem("token"));

    if (token) {
        return { Authorization: `Bearer ${token}`, };

    } else {
        return {};
    };
};

export default authHeader;
