import { ACTION_TYPES } from "../actions/sessionActions";
import {login} from "../services/user/userService";

const initState = {
    session: null,
    isLoading: false,
    loaded: false,
    fetchError: null
}

const sessionReducer = (state = initState, action) => {
    const payload = action.payload;
    switch (action.type) {
        case ACTION_TYPES.FETCH_SESSION_PENDING:
            console.log("fetching session");
            return {
                ...state,
                isLoading: true,
                loaded: false,
                fetchError: null
            };
        case ACTION_TYPES.FETCH_SESSION_SUCCESS:
            console.log("success");
            return {
                ...state,
                session: payload,
                isLoading: false,
                loaded: true,
                fetchError: null
            };
        case ACTION_TYPES.FETCH_SESSION_FAILURE:
            console.log("failed");
            return {
                ...state,
                session: null,
                isLoading: false,
                loaded: true,
                fetchError: payload
            };
        default:
            return state
    }
};

export default sessionReducer;
