import {getChargerState} from "./checkChargerStateService";
import getChargerOperatingMode from "./getChargerOperatingMode";

/**
 * @author Sven
 * Service for polling operatingMode of chargers
 */

let pollingTimeout = null;
var selectedId = -1;
let doPoll;

/**
 * Invokes a poll without waiting
 *
 * @returns {void}
 */
const invokePoll = () => {
    doPoll();
}

/**
 * Communicates with backend to get the operatingMode
 * Calls the callback function with the opearting mode
 *
 * @param pollingId ID of the charger
 * @param callback Callback function
 */
const pollNew = (pollingId, callback) => {
    clearTimeout(pollingTimeout);
        doPoll = async () => {
            selectedId = pollingId;
            await getChargerOperatingMode(pollingId).then(x => {
                callback(x);
            }).catch(e => {
                callback(-1);
            });
            pollingTimeout = setTimeout(doPoll, 10000);
    }
    doPoll();
}

export {pollNew, selectedId, invokePoll};
