import React, {useEffect, useRef, useState} from "react";
import roundToNearest5 from "../services/math/roundToNearest5";
import {addHours, dateToHHMM, nToNN} from "../services/time/timeService";


const TimeSelector = ({onChangeHour, onChangeMinute}) => {

    const [curDate, setCurDate] = useState(new Date());

    const [availableMinutes, setAvailableMinutes] = useState(null);
    const [selectedHour, setSelectedHour] = useState(curDate.getHours());
    const [selectedMinute, setSelectedMinute] = useState(curDate.getMinutes());

    const selectedDate = useRef(new Date());

    const assembleDate = () => {
        selectedDate.current.setHours(selectedHour);
        selectedDate.current.setMinutes(selectedMinute);
        selectedDate.current.setSeconds(0);

        // It is the next day
        if(selectedHour < curDate.getHours()) {
            selectedDate.current.setDate(curDate.getDate() + 1);
        } else {
            selectedDate.current.setDate(curDate.getDate());
        }

        console.log(selectedDate)
    }


    const pollTime = setInterval(() => {
        const curDate = new Date();
        if (curDate.getMinutes() !== curDate.getMinutes()) {
            clearInterval(pollTime);
            setCurDate(new Date());
        }
    }, 1000);


    useEffect(() => {
        let isMounted = true;


        if(isMounted) {
            // To ensure that if the clock is for example 19:55, you can only start selecting from 20:00 and up
            if(curDate.getMinutes() + 5 >= 55) {
                setSelectedHour(curDate.getHours() + 1);
                setSelectedMinute(0);
                updateAvailableMinutes();
            } else {
                setSelectedMinute(roundToNearest5(curDate.getMinutes() + 5))
            }
        }

        return (() => {
            clearInterval(pollTime);
            isMounted = false;
        });
    }, []);

    useEffect(() => {
        let isMounted = true;

        if(isMounted) {
            updateAvailableMinutes();
        }

        return (() => {
            isMounted = false;
        })

    }, [selectedHour]);

    useEffect(() => {
        let isMounted = true;

        if(isMounted) {
            assembleDate();
        }

        return (() => {
            isMounted = false;
        })
    }, [selectedHour, selectedMinute]);

    const updateAvailableMinutes = () => {
        let avail = [];
        // DO NOT CHANGE TO ==
        switch(curDate.getHours() === selectedHour) {
            case true:
                for(let i = roundToNearest5(curDate.getMinutes()) + 5; i < 60; i+=5) {
                    avail.push(i);
                }
                setAvailableMinutes(avail);
                break;
            default:
                for(let i = 0; i < 60; i += 5) {
                    avail.push(i);
                }
                setAvailableMinutes(avail);
                break;
        }
    }

    return (
        <div className="timeSelectorContainer">
            <select onChange={(e) => {
                setSelectedHour(e.target.value);
            }} style={{width: "33%"}} name="hour">
                {curDate.getMinutes() + 5 >= 55 ?
                    [...Array(9).keys()].map(x => {
                        return (
                            <option value={addHours(curDate, x + 1).getHours()}>{nToNN(addHours(curDate, x + 1).getHours())}</option>
                        )
                    })
                    :
                    [...Array(9).keys()].map(x => {
                        return (
                            <option value={addHours(curDate, x).getHours()}>{nToNN(addHours(curDate, x).getHours())}</option>
                        )
                    })
                }
            </select>
            <h3 style={{display: "inline-block", marginLeft: "3%", marginRight: "3%"}}>:</h3>
            <select onChange={(e) => {
                setSelectedMinute(e.target.value)
            }} style={{width: "33%"}} name="minute">
                {availableMinutes !== null ?
                    availableMinutes.map((minute) => {
                        return (
                            <option key={minute} value={minute}>{nToNN(minute)}</option>
                        )
                    })
                    :
                    null
                }
            </select>
        </div>
    )
}

export default TimeSelector;
