/**
 * @author Magnus & Sven
 *
 * Service for various operations relating to time.
 */

/**
 * Converts a date object to a HHMM string.
 *
 * @param time the date object
 * @returns {`${string}:${string}`}
 */

const dateToHHMM = (time) => {
    const hours = time.getHours() > 9 ? `${time.getHours()}` : `0${time.getHours()}`;
    const minutes = time.getMinutes() > 9 ? `${time.getMinutes()}` : `0${time.getMinutes()}`;
    return `${hours}:${minutes}`;
}

let interval;

/**
 * Periodically compares time with now time and calls callback if time is older than now time
 *
 * @param time the object that will be compared with now time
 * @param callback the callback
 *
 * @returns {void}
 */

const pollTime = (time, callback) => {
    if(interval != null) clearInterval(interval);
    interval = setInterval(() => {
        const now = new Date();
        if(now > time) {
            callback(new Date());
        }
    }, 1000);
}

/**
 * Stops pollTime
 *
 * @returns {void}
 */

const stopPolling = () => {
    if(interval != null) clearInterval(interval);
    interval = null;
}

/**
 * Converts a single digit number to a double digit number (eg. 9 -> 09)
 *
 * @param n the number to be converted
 * @return {string}
 */

const nToNN = (n) => {
    return (n > 9 ? `${n}` : `0${n}`);
}

/**
 * Adds minutes to a date object
 *
 * @param date the date object
 * @param minutes number of minutes
 * @return {Date}
 */

const addMinutes = (date, minutes) => {

    const now = new Date();

    const newDate = new Date(date.getTime() + minutes*60000);

    if (newDate < now) {
        return now;
    }
    return newDate;
}

/**
 * Adds hours to a date object
 *
 * @param date the date object
 * @param hours number of hours
 * @return {Date}
 */

const addHours = (date, hours) => {

    const now = new Date();

    const newDate = new Date(date.getTime() + hours*3600000);

    if (newDate < now) {
        return now;
    }
    return newDate;
}

export {addMinutes, addHours, dateToHHMM, nToNN, pollTime};
