import React, {useState, useEffect} from 'react';
import {addHours, addMinutes, nToNN, pollTime} from '../services/time/timeService'
import '../stylesheets/TimeSelector.css';

/**
 * @author Magnus & Sven
 *
 * Komponent som lar en velge tidspunkt.
 * @component
 */

const TimeSelector = (props) => {

    const [time, setTime] = useState(new Date());

    useEffect(() => {

        /**
         * Metode som setter tid i parent-komponent
         *
         * @memberOf TimeSelector
         * @returns {void}
         */
        let isMounted = true;

        if(isMounted) {
            props.setTime(time);
        }

        return () => {
            isMounted = false;
        }
    }, [props.getTime]);

    pollTime(time, (e) => setTime(e));

    let curDate = new Date();
    let availableHours = [];

    for(let i = 0; i <= curDate.getHours() + 8; i++) {
        availableHours.push(curDate.getHours() + i);
    }

    let available

    return (
        <div className="row">
            <div className="timeContainer">
                <div className="column">
                    <select name="hours" value={nToNN(curDate.getHours())}>
                        {availableHours.map(x => {
                            return (
                                <option value={nToNN(x)}>{nToNN(x)}</option>
                            )
                        })}
                    </select>
                </div>

                <h4>:</h4>

                <div  className="column">
                    <select name="minutes" value={nToNN(curDate.getMinutes())}>
                        
                    </select>
                </div>

            </div>
        </div>
    )
}

export default TimeSelector;
