import axios from "axios";
import {ip, tomcatPort} from "../misc/constants";
import authHeader from "../services/user/auth-header";

import history from "../history";

const URL = `http://${ip}:${tomcatPort}/api/user/`;

/**
 * userActions har dispatch av actions knyttet til bruker
 * Login, Logout, Register, Auth
 * @author Magnus
 */

export const ACTION_TYPES = {
    LOGIN_USER: 'LOGIN_USER',
    LOGIN_SUCCESS: 'LOGIN_SUCCESS',
    LOGIN_FAILURE: 'LOGIN_FAILURE',
    REGISTER_USER: 'REGISTER_USER',
    REGISTER_SUCCESS: 'REGISTER_SUCCESS',
    REGISTER_FAILURE: 'REGISTER_FAILURE',
    AUTH_USER: 'AUTH_USER',
    AUTH_SUCCESS: 'AUTH_SUCCESS',
    AUTH_FAILURE: 'AUTH_FAILURE',
    LOGOUT_USER: 'LOGOUT_USER'
};

/**
 * Metode som dispatcher actions knyttet til innlogging.
 * @param user
 * @return async function
 */

export const loginUser = (user) => {
    return async (dispatch) => {
        dispatch({ type: ACTION_TYPES.LOGIN_USER, payload: user});
        await axios
        .post(`${URL}login`, user)
        .then((response) => {
            const string = JSON.stringify(response.data);
            const arr = string.split(" ");
            const username = arr[0].replace('"', '');
            const token = arr[1].replace('"', '');
            const user = {
                username: username,
                JWT: token
            }
            localStorage.setItem("token", JSON.stringify(user.JWT));
            dispatch({ type: ACTION_TYPES.LOGIN_SUCCESS, payload: user});
            history.push("/");
        })
        .catch((error) => {
            dispatch({ type: ACTION_TYPES.LOGIN_FAILURE, payload: error});
        });
    };
};

/**
 * Metode som dispatcher actions knyttet til registrering
 * @param user
 * @return async function
 */

export const registerUser = (user) => {
    return async (dispatch) => {
        dispatch({ type: ACTION_TYPES.REGISTER_USER, payload: user});
        axios.post(`${URL}register`, user)
            .then((response) => {
                dispatch({ type: ACTION_TYPES.REGISTER_SUCCESS, payload: user});
                history.push("/login");
            }).catch((error) => {
                dispatch({ type: ACTION_TYPES.REGISTER_FAILURE, payload: error});
        });
    };
};

/**
 * Metode som dispatcher actions knyttet til utlogging
 * @return function
 */

export const logoutUser = () => {
    return (dispatch) => {
        localStorage.removeItem("token");
        dispatch({ type: ACTION_TYPES.LOGOUT_USER });
        history.push("/login");
    }
}

/**
 * Metode som dispatcher actions knyttet til autorisering
 * @return async function
 */

export const authUser = () => {
    return async (dispatch) => {
        dispatch( { type: ACTION_TYPES.AUTH_USER });
        const config = {
            headers: authHeader()
        };
        await axios.post(`${URL}authenticate`, {}, config)
            .then((response) => {
                dispatch({ type: ACTION_TYPES.AUTH_SUCCESS, payload: response});
            }).catch((error) => {
                dispatch({ type: ACTION_TYPES.AUTH_FAILURE, payload: error});
        });
    };
};
